import Layout from '../components/MyLayout.js';
import Appartement from '../components/Appartement.js';
import AppartementList from '../components/AppartementList.js';
import Link from 'next/link';

const Index = props => (

  <Layout>
    <h1>Liste des appartements</h1>

    <Appartement></Appartement>
    <AppartementList></AppartementList>

    <ul>
      {props.data.map(el => (
        <li key={el.id}>
          <img src={el.acf.image} />
          <Link as={`/appartement/${el.id}`} href={`/post?id=${el.id}`}>
            <a>{el.slug}</a>
          </Link>
          <p>${el.acf.price} per night</p>
          <p>{new Array(parseInt(el.acf.rating)).fill('⭐️️️️️')}</p>
        </li>
      ))}
    </ul>  
    <style jsx>{`
      img {
        width: 100px;
        height: 100px;
      }
    `}</style>
  </Layout>

);

