import Link from 'next/link';

const linkStyle = {
  marginRight: 15
};

const Header = () => (
  <div>
    <Link href="/">
      <a style={linkStyle}>Home</a>
    </Link>
    <Link href="/about">
      <a style={linkStyle}>About</a>
    </Link>
  </div>
);

const Appartment = () => (
  <div>

    {props.data.map(el => (
      <li key={el.id}>
        <img src={el.acf.image} />

        <Link as={`/appartement/${el.slug}`} href={`/post?id=${el.id}`}>
            <a><h1>{el.slug} {favorites.includes(id) && '❤️'}</h1></a>
        </Link>
        <p>${el.acf.price} per night</p>
        <p>{new Array(parseInt(el.acf.rating)).fill('⭐️️️️️')}</p>

        <button onClick={e => toggleFav(e, id)}>
          {favorites.includes(id) ? 'Remove favorite' : 'Set favorite'}
        </button>
      </li>
    ))}
  </div>
);

Index.getInitialProps = async function() {
  const res = await fetch('http://apirl.xyz/wp-json/wp/v2/appartments?_fields=id,title,slug,acf');
  const data = await res.json();

  console.log(`Show data fetched. Count: ${data.length}`);

  return {data};
};

export default Appartement;
