import Layout from '../components/MyLayout.js';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';
import "../css/style.css"

const Index = props => (

  <Layout>
    <h1>Liste des appartements</h1>

    <section>
      {props.data.map(el => (
        <article key={el.id}>
          <img class="img_grid" src={el.acf.image} />
          <div class="desc">
            <Link as={`/appartement/${el.slug}`} href={`/post?id=${el.slug}`}>
              <a class="titre">{el.title.rendered}</a>
            </Link>
            <div class="line"></div>

            <p class="price">${el.acf.price} per night</p>
            <p class="rating">{new Array(parseInt(el.acf.rating)).fill('⭐️️️️️')}</p>
            <p>{el.acf.available}</p>
          </div>
        </article>
      ))}
    </section>  
  </Layout>

);

Index.getInitialProps = async function() {
  const res = await fetch('http://apirl.xyz/wp-json/wp/v2/appartments?_fields=id,title,slug,acf');
  const data = await res.json();

  console.log(`Show data fetched. Count: ${data.length}`);

  return {data};
};

export default Index;
