import Layout from '../components/MyLayout.js';
import Appartement from '../components/Appartement.js';
import AppartementList from '../components/AppartementList.js';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';

const Index = props => (
  <Layout>
    <h1>Liste des appartements</h1>

    <Appartement></Appartement>
    <AppartementList></AppartementList>

    <ul>
      {props.data.map(el => (
        <li key={el.id}>
          <img src={el.acf.image} />
          <Link as={`/appartement/${el.slug}`} href={`/post?id=${el.id}`}>
            <a>{el.slug}</a>
          </Link>
        </li>
      ))}
    </ul>  
    <style jsx>{`
      img {
        width: 100px;
        height: 100px;
      }
    `}</style>

    <div>
        Apartment List<br/>
        <button onClick={this.toggleShow}>
          {showAll ? 'Show only available' : 'ShowAll'}
        </button><br/>
        <Searchbar
          searchValue={searchValue}
          handleInputText={this.handleInputText}
          found={availableAppartments.length}
          total={appartments.length}
          />
        {isLoading ?
          <p>Loading ...</p>
          :
          <AppartmentList
            appartments={availableAppartments}
            favorites={favorites}
            toggleFav={this.toggleFavorite}
          />
        }
      </div>
  </Layout>

);

Index.getInitialProps = async function() {
  const res = await fetch('http://apirl.xyz/wp-json/wp/v2/appartments?_fields=id,title,slug,acf');
  const data = await res.json();

  console.log(`Show data fetched. Count: ${data.length}`);

  return {data};
};

export default Index;
