import Layout from '../components/MyLayout.js';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';

const Index = props => (

  <Layout>
    <h1>Liste des appartements</h1>

    <ul>
      {props.data.map(el => (
        <li key={el.id}>
          <img src={el.acf.image} />
          <Link as={`/appartement/${el.id}`} href={`/post?id=${el.id}`}>
            <a>{el.slug}</a>
          </Link>
          <p>${el.acf.price} per night</p>
          <p>{new Array(parseInt(el.acf.rating)).fill('⭐️️️️️')}</p>
          <p>{el.acf.available}</p>
        </li>
      ))}
    </ul>  
    <style jsx>{`
      img {
        width: 100px;
        height: 100px;
      }
    `}</style>
  </Layout>

);

Index.getInitialProps = async function() {
  const res = await fetch('http://apirl.xyz/wp-json/wp/v2/appartments?_fields=id,title,slug,acf');
  const data = await res.json();

  console.log(`Show data fetched. Count: ${data.length}`);

  return {data};
};

export default Index;
