import Link from 'next/link';

const linkStyle = {
  marginRight: 15
};

const Header = () => (
  <div>
    <Link href="/">
      <a style={linkStyle}>Home</a>
    </Link>
    <Link href="/about">
      <a style={linkStyle}>About</a>
    </Link>
  </div>
);

const Appartment = () => (
  <div>
    <img src={acf.image} alt="lol"/>
    <h1>
      <svg height="20" width="20">
        <circle cx="10" cy="10" r="10" fill={acf.available ? 'green' : 'red'} />
      </svg>
      {title.rendered}
      {favorites.includes(id) && '❤️'}
    </h1>

    <button onClick={e => toggleFav(e, id)}>
      {favorites.includes(id) ? 'Remove favorite' : 'Set favorite'}
    </button>

    <p>${acf.price} per night</p>
    <p>{new Array(parseInt(acf.rating)).fill('⭐️️️️️')}</p>

    <ul>
      {props.data.map(el => (
          <li key={el.id}>
          <img src={el.acf.image} />
          <Link as={`/appartement/${el.slug}`} href={`/post?id=${el.id}`}>
              <a>{el.slug}</a>
          </Link>
          </li>
      ))}
    </ul>
  </div>
);

Index.getInitialProps = async function() {
  const res = await fetch('http://apirl.xyz/wp-json/wp/v2/appartments?_fields=id,title,slug,acf');
  const data = await res.json();

  console.log(`Show data fetched. Count: ${data.length}`);

  return {data};
};

export default Appartement;
