import Link from 'next/link';

const Appartment = () => (
  <div>
    {props.data.map(el => (
      <li key={el.id}>
        <img src={el.acf.image} />

        <Link as={`/appartement/${el.slug}`} href={`/post?id=${el.id}`}>
            <a><h1>{el.slug} {favorites.includes(id) && '❤️'}</h1></a>
        </Link>
        <p>${el.acf.price} per night</p>
        <p>{new Array(parseInt(el.acf.rating)).fill('⭐️️️️️')}</p>

        <button onClick={e => toggleFav(e, id)}>
          {favorites.includes(id) ? 'Remove favorite' : 'Set favorite'}
        </button>
      </li>
    ))}
  </div>
);

