import Layout from '../components/MyLayout.js';
// import fetch from 'isomorphic-unfetch';

const Post = props => (
  <Layout>
  {props.data.map(el => (
    <li key={el.id}>
      <Link as={`/p/${el.id}`} href={`/post?id=${el.id}`}>
        <a>{el.slug}</a>
      </Link>
    </li>
  ))}
    <h1>{props.data.titre.rendered}</h1>


  </Layout>
);

Post.getInitialProps = async function(context) {
    const { id } = context.query;
    const res = await fetch(`https://apirl.xyz/wp-json/wp/v2/appartments/${id}?_fields=id,title,slug,acf`);
    const data = await res.json();

    console.log(`Fetched show: ${data.title.rendered}`);

    return { data };
};

export default Post;
